using Epam.WebPages.Common;
using Epam.WebPages.Entities;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;
using System.Web.Helpers;

namespace Epam.WebPages.UIweb.Models
{
    public static class AwardModel
    {
        static string defaultawardimage = "";
            public static IEnumerable<Award> GetAllAwards()
        {
            return DependencyResolver.AwardLogic.GetAllAwards();
        }
        public static void AddAward(string title, WebImage image)
        {
            if (image != null)
            {
                string base64string = Convert.ToBase64String(image.GetBytes());
                DependencyResolver.AwardLogic.AddAward(title, base64string);
            }
            else
            {
                DependencyResolver.AwardLogic.AddAward(title, defaultawardimage);
            }

        }
        public static bool DeleteAward(string id)
        {
            return DependencyResolver.AwardLogic.DeleteAward(Guid.Parse(id));
        }
    }
}